import React from 'react';
import "./Home.css"
import { useEffect, useState } from "react"
import axios from "axios"

export default function Home() {

  const [pokemons, setPokemon] = useState([])
  var Name = useState('');
  var Img = useState('');
  var Desc = useState('');

  var dropdownElem = document.getElementById("dropdown");

  //Fetch data from API to render to home page.
  useEffect(() => {
    const fetchPokemon = async () => {

      // Fetch API data from DB hosted on Render.
      try{

        const response = axios.get('http://localhost:4000/api/pokemon')

        .then((response) => {
          (setPokemon(response.data));
          console.log(response.data)
        });
      }

      catch (error) {
          console.log("Failed to get pokemon", error)
      }
    }

    //call function
    fetchPokemon()
  }, 
[])

  useEffect(() => {
    const dropdownElem = document.getElementById("dropdown");
    dropdownElem.innerHTML = ""; // Clear existing options

    const select = document.createElement("option");
    select.text = 'Choose Pokemon';
    select.value = '';
    select.selected = true;
    select.disabled = true;
    dropdownElem.add(select);

    pokemons.forEach(pokemon => {
      const option = document.createElement("option");
      option.text = pokemon.name;
      option.value = pokemon._id;
      dropdownElem.add(option);
    });


  }, [pokemons]); // Run this effect whenever `pokemons` changes

  function onDropdownSelect() {
    const dropdownElem = document.getElementById("dropdown");
    const val = dropdownElem.value

    const nameElem = document.getElementById("names")
    const imgElem = document.getElementById("names")
    const descElem = document.getElementById("names")


    const pokemon = pokemons.find(pokemon => pokemon._id === val)

    return Name = pokemon.name

  }

  return (
    <>
      <head>
        <meta charSet="UTF-8" />
        <meta name="viewport" content="width=device-width, initial-scale=1.0" />

        {/* Links */}
        <link rel="stylesheet" href="./css/style.css" />
        <link rel="icon" type="image/png" sizes="16x16" href="/favicons/favicon-16x16.png" />
        <meta name="msapplication-TileColor" content="#ffffff" />
        <meta name="theme-color" content="#ffffff" />

        <title>Pokedex</title>
      </head>
      <body>
        <main>
        <div>
        <select value='' id="dropdown" className="dropdown" onChange={onDropdownSelect}> 

        </select>
        </div>

          <img src={Img} alt="pokemon" className="pokemon_image" id='imgs'/>

          <h1 className="pokemon_data">
            <span className="pokemon_name" id="names">
              {Name}
            </span>
          </h1>

          <form >
            <p
              className="input_search"
              id='descs'
            > {Desc} </p>
          </form>

          <div className="buttons">
            <button className="button btn-prev">Prev &lt;</button>
            <button className="button btn-next">Next &gt;</button>
          </div>

          <img
            src="https://pokedex-conrado.vercel.app/images/pokedex.png"
            alt="pokedex"
            className="pokedex"
          />
        </main>
      </body>
    </>
  );
}
