import React from 'react';
import "./Home.css"
import { useEffect, useState } from "react"
import axios from "axios"

export default function Home({Name, Img, Desc}) {

  const [pokemons, setPokemon] = useState([])

  var dropdownElem = document.getElementById("dropdown");

  //Fetch data from API to render to home page.
  useEffect(() => {
    const fetchPokemon = async () => {

      // Fetch API data from DB hosted on Render.
      try{
        const response = axios.get('https://blogapp-backend-15kn.onrender.com/api/pokemons')
        .then((response) => {
          (setPokemon(response.data));
        });
      }

      catch (error) {
          console.log("Failed to get pokemon", error)
      }
    }

    //call function
    fetchPokemon()
  }, 

  [])


  for (let i = 0; i < pokemons.length; i++) {
    var option = document.createElement("option");
    option.text = pokemons[i].name;
    option.value = pokemons[i].id;
    dropdownElem.add(option);
  }

  dropdownElem.onchange = onDropdownSelect(dropdownElem.value);

  function onDropdownSelect({ID}) {

    const pokemon = pokemons.find(pokemon => pokemon.id === parseInt(ID))

    return(
      Name = pokemon.name,
      Img = pokemon.img,
      Desc = pokemon.desc
    )


  }


  return (
    <>
      <head>
        <meta charSet="UTF-8" />
        <meta name="viewport" content="width=device-width, initial-scale=1.0" />

        {/* Links */}
        <link rel="stylesheet" href="./css/style.css" />
        <link rel="icon" type="image/png" sizes="16x16" href="/favicons/favicon-16x16.png" />
        <meta name="msapplication-TileColor" content="#ffffff" />
        <meta name="theme-color" content="#ffffff" />

        <title>Pokedex</title>
      </head>
      <body>
        <main>
        <div>
        <select id="dropdown" className="dropdown"> 

        <option value="" disabled selected hidden>Choose Pokemon</option>

        </select>
        </div>

          <img src={Img} alt="pokemon" className="pokemon_image" />

          <h1 className="pokemon_data">
            <span className="pokemon_name">
              {Name}
            </span>
          </h1>

          <form >
            <p
              className="input_search"
            > {Desc} </p>
          </form>

          <div className="buttons">
            <button className="button btn-prev">Prev &lt;</button>
            <button className="button btn-next">Next &gt;</button>
          </div>

          <img
            src="https://pokedex-conrado.vercel.app/images/pokedex.png"
            alt="pokedex"
            className="pokedex"
          />
        </main>
      </body>
    </>
  );
}
