import React, { useEffect, useState } from 'react';
import axios from "axios";

export default function Home({ Name, Img, Desc }) {
  const [pokemons, setPokemons] = useState([]);

  useEffect(() => {
    const fetchPokemon = async () => {
      try {
        const response = await axios.get('http://localhost:4000/api/pokemon');
        setPokemons(response.data);
      } catch (error) {
        console.log("Failed to get pokemon", error);
      }
    };

    fetchPokemon();
  }, []); // Empty dependency array ensures this effect runs only once, after initial render

  useEffect(() => {
    const dropdownElem = document.getElementById("dropdown");
    dropdownElem.innerHTML = ""; // Clear existing options

    pokemons.forEach(pokemon => {
      const option = document.createElement("option");
      option.text = pokemon.name;
      option.value = pokemon._id;
      dropdownElem.add(option);
    });
  }, [pokemons]); // This effect will run whenever `pokemons` state changes

  return (
    <>
      <head>
        {/* Head content goes here */}
      </head>
      <body>
        <main>
          <div>
            <select id="dropdown" className="dropdown">
              <option value="" disabled selected hidden>Choose Pokemon</option>
            </select>
          </div>

          <img src={Img} alt="pokemon" className="pokemon_image" />

          <h1 className="pokemon_data">
            <span className="pokemon_name">
              {Name}
            </span>
          </h1>

          <form>
            <p className="input_search">
              {Desc}
            </p>
          </form>

          <div className="buttons">
            <button className="button btn-prev">Prev &lt;</button>
            <button className="button btn-next">Next &gt;</button>
          </div>

          <img
            src="https://pokedex-conrado.vercel.app/images/pokedex.png"
            alt="pokedex"
            className="pokedex"
          />
        </main>
      </body>
    </>
  );
}
